input = input.replace(".", "0").replace("#", "1")
input = input.split("\n")[1:-1]

rows = len(input)
col = len(input[0])

# convert to np for easy matrix math
np_input = np.zeros((rows, col))
for x in range(len(input)):
    for y in range(len(input[0])):
        if input[x][y] == "1":
            np_input[x][y] = 1

input_copy = np.copy(np_input)
addition = 0
for x in range(len(input)):
    empty = True
    for y in range(len(input[0])):
        if input[x][y] == "1":
            empty = False
            break 
    if empty:
        input_copy = np.insert(input_copy, x+addition, 0, axis=0)
        addition += 1

