input = input_copy

galaxies = []
for x in range(len(input)):
    for y in range(len(input[0])):
        if input[x][y] == 1:
            galaxies.append((x,y))

# just simple manhattan distance
distances = 0
for x in range(len(galaxies)):
    for y in range(x+1,len(galaxies)):
        print(x, y, np.abs(galaxies[x][0]- galaxies[y][0]) + np.abs(galaxies[x][1]- galaxies[y][1]))
        distances+= np.abs(galaxies[x][0]- galaxies[y][0]) + np.abs(galaxies[x][1]- galaxies[y][1])

